import Pool from './modules/Pool';
import Resource from './modules/Resource';
import ResourceIdentifier from './modules/ResourceIdentifier';
import Relationship from './modules/Relationship';
import Link from './modules/Link';
import RESTful from './modules/RESTful';

/**
 * @see http://jsonapi.org/format/#document-meta
 * @typedef {object} MetaObject
 */

export default {
  Pool,
  Resource,
  ResourceIdentifier,
  Relationship,
  Link,
  RESTful
};

import lodash from 'lodash';
import underscoreDB from 'underscore-db';

/**
 * @external {LowDB} https://github.com/typicode/lowdb
 */


function lowChain(_, array) {
  let chain = _.chain(array);

  _.functions(chain)
  .forEach(function(method) {
    chain[method] = _.flow(chain[method], function(arg) {
      let res = arg.value ? arg.value() : arg;
      return res;
    });
  });

  return chain;
}


/**
 * @desc client-side database.
 * @return {LowDB}
 * @example
 * let db = new DB();
 * db('comment').insert({ content: 'hello' });
 */
function DB() {
  let _ = lodash.runInContext();
  _.mixin(underscoreDB);

  let value = _.prototype.value;
  _.prototype.value = function() {
    let res = value.apply(this, arguments);
    return res;
  };

  function db(key) {
    let array;
    if (db.object[key]) {
      array = db.object[key];
    }
    else {
      array = db.object[key] = [];
    }

    let short = lowChain(_, array);
    short.chain = function() {
      return _.chain(array);
    };
    return short;
  }

  // Expose lodash instance
  db._ = _;

  // Expose database object
  db.object = {};

  return db;
}

DB.stringify = function(obj) {
  return JSON.stringify(obj, null, 2);
};

DB.parse = function(str) {
  return JSON.parse(str);
};


export default DB;

import _ from 'lodash';

/**
 * @see http://jsonapi.org/format/#document-links
 * @typedef {(object|string)} LinkObject
 * @property {!string} href
 * @property {?MetaObject} meta
 */

/**
 * @see http://jsonapi.org/format/#document-links
 * @example
 * let link1 = new Link('/api/resource');
 * let link2 = new Link({
 *   href: '/api/resource',
 *   meta: {
 *     foo: 'bar'
 *   }
 * });
 * link1.href === link2.href  // true
 * link1.serialize()  // { href: 'api/resource' };
 * link2.serialize()  // { href: 'api/resource', meta: { foo: 'bar' } };
 */
export default class Link {

  /**
   * @param {LinkObject} link
   */
  constructor(link) {
    /** @type {string} */
    this.href = null;
    /** @type {MetaObject} */
    this.meta = null;
    this.deserialize(link);
  }

  /**
   * @return {LinkObject} link
   */
  serialize() {
    return _.omit({
      href: this.href,
      meta: this.meta
    }, _.isEmpty);
  }

  /**
   * @param {LinkObject} link
   */
  deserialize(link) {
    if (_.isString(link)) {
      this.href = link;
      this.meta = {};
    }
    else {
      this.href = link.href;
      this.meta = link.meta;
    }
  }

}

